package ex;

public class QuestionA4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 10;
		
		for(int i=1;i<=10;i++) {
			System.out.println(i + "x" + num + "=" + (i*num));
		}
		
		System.out.println();
		
		int i = 1;
		while(i<=10) {
			System.out.println(i + "x" + num + "=" + (i*num));
			i++;
		}
	}

}
